import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpenseTable from './ExpenseTable';
import ExpenseForm from './ExpenseForm';
import ExpenseDetails from './ExpenseDetails';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [expenseAmt, setExpenseAmt] = useState(0);
    const [incomeAmt, setIncomeAmt] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'))
    }, [])

    useEffect(() => {
        const amounts = expenses.map((item) => item.amount);
        console.log(amounts);

        const income = amounts.filter((item) => item > 0)
            .reduce((acc, item) => (acc += item), 0);
        console.log('income :', income);

        const exp = amounts.filter((item) => item < 0)
            .reduce((acc, item) => (acc += item), 0) * -1;
        console.log('exp :', exp);

        setIncomeAmt(income);
        setExpenseAmt(exp);
    }, [expenses]);

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        setTimeout(() => {
            navigate('/login');
        }, 1000)
    }

    const fetchExpenses = async () => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            }
            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login')
                return;
            }
            const result = await response.json();
            console.log(result.data);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    }

    const addExpenses = async (data) => {
        try {
            const url = `${APIUrl}/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    "Content-Type": 'application/json'
                },
                method: "POST",
                body: JSON.stringify(data)
            }
            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login')
                return;
            }
            const result = await response.json();
            console.log(result.data);
            setExpenses(result.data);
            handleSuccess(result.message);
        } catch (err) {
            handleError(err);
        }
    }

    useEffect(() => {
        fetchExpenses();
    }, []);

    const handledeleteExpense = async (expenseId) => {
        try {
            const url = `${APIUrl}/expenses/${expenseId}`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    "Content-Type": 'application/json'
                },
                method: "DELETE"
            }
            const response = await fetch(url, headers);
            if (response.status === 403) {
                navigate('/login')
                return;
            }
            const result = await response.json();
            console.log(result.data);
            setExpenses(result.data);
            handleSuccess(result.message);
        } catch (err) {
            handleError(err);
        }
    }

    return (
        <div className="p-6">
            <div className="user-section flex justify-between items-center mb-4">
                <h1 className="text-2xl font-bold">Welcome {loggedInUser}</h1>
                <button className="bg-purple-600 text-white text-lg rounded-md py-2 px-4" onClick={handleLogout}>Logout</button>
            </div>
            <ExpenseDetails incomeAmt={incomeAmt} expenseAmt={expenseAmt} />
            <ExpenseForm addExpenses={addExpenses} />
            <ExpenseTable expenses={expenses} handledeleteExpense={handledeleteExpense} />
            <ToastContainer />
        </div>
    )
}

export default Home;
