import React, { useState } from 'react'
import { handleError } from '../utils';

function ExpenseForm({ addExpenses }) {

    const [expenseInfo, setExpenseInfo] = useState({
        amount: '',
        text: ''
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        console.log(name,value)
        const copyExpenseInfo = { ...expenseInfo };
        copyExpenseInfo[name] = value;
        setExpenseInfo(copyExpenseInfo);
    }

    const handleExpense=(e)=>{
        e.preventDefault()
        console.log(expenseInfo)
        const {text,amount}=expenseInfo
        if(!text || !amount){
            handleError("All fields are required")
            return;
        }

        setTimeout(() => {
            setExpenseInfo({text:"", amount:''})
        }, 1000);
        
        addExpenses(expenseInfo)
    }

    return (
        <div className='container'>
            <h1>Expense Tracker</h1>
            <form onSubmit={handleExpense}>
                <div>
                    <label htmlFor='text'>Expense Detail</label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='text'
                        placeholder='Enter your Expense Detail...'
                        value={expenseInfo.text}
                    />
                </div>
                <div>
                    <label htmlFor='amount'>Amount</label>
                    <input
                        onChange={handleChange}
                        type='number'
                        name='amount'
                        placeholder='Enter expense in(-ve)'
                        value={expenseInfo.amount}
                    />
                </div>
                <button type='submit'>Add Expense</button>
            </form>
        </div>
    )
}

export default ExpenseForm
